create table user_roles (user_id int8 not null, roles varchar(255));
create table users (id int8 generated by default as identity, active boolean, applied boolean default 'f', online boolean default 'f', email varchar(255), activation_code varchar(255), photo varchar(255), password varchar(255) not null, username varchar(255) not null, owner_id int8, primary key (id));
